  (2,NULL,'01 . Header','<?php\r\n// REX_VALUE[11] bestimmt, ob Farb- oder Verlaufsfilter\r\n// REX_VALUE[12] definiert die Deckkraft\r\necho \'\r\n<div class=\"section section-header\">\r\n    <div class=\"parallax pattern-image filter filter-REX_VALUE[11] opREX_VALUE[12]\">\';\r\n\r\n		// Partikel-Animation nur zeigen, wenn ausgewählt und kein Video (wegen Performance). Außerdem nicht im Backend zeigen\r\n		if (!rex::isBackend() && \"REX_VALUE[18]\" == \'1\' && \"REX_VALUE[9]\" == \'photo\') {\r\n			echo \'\r\n			<div class=\"particle\"></div>\';\r\n		}\r\n\r\n		// Foto als Hintergrund\r\n		if (\"REX_VALUE[9]\" == \'photo\') {\r\n\r\n            if (\"REX_MEDIA[1]\" != \'\') {\r\n                $media = rex_media::get(\"REX_MEDIA[1]\");\r\n                $mediatitle = $media->getValue(\'title\');\r\n                echo \'\r\n				<img src=\"\'.rex_url::base(\'index.php?rex_media_type=header&rex_media_file=REX_MEDIA[1]\').\'\" style=\"position: relative; z-index: 1;\" alt=\"\'.$mediatitle.\'\">\';\r\n	        }\r\n\r\n		} else {\r\n\r\n    		// Video als Hintergrund\r\n			if (\"REX_MEDIA[2]\" != \'\') {\r\n				echo \'\r\n				<video class=\"bgvid\" preload=\"auto\" loop=\"loop\" autoplay=\"false\" muted=\"muted\" volume=\"0\">\r\n				<source src=\"\'.rex_url::base(\'media/REX_MEDIA[2]\').\'\" type=\"video/mp4\">Video not supported</video>\';\r\n			}\r\n\r\n		}\r\n\r\n		echo \'\r\n        <div class=\"container\">\r\n		    <div class=\"content\">\';\r\n			?>\r\n\r\n				REX_VALUE[id=\'1\' prefix=\'<p>\' suffix=\'</p>\']\r\n				REX_VALUE[id=\'2\' prefix=\'<h1>\' suffix=\'</h1>\']\r\n\r\n				<?php\r\n				// Trennstrich\r\n				if (\"REX_VALUE[3]\" != \'\') {\r\n					echo \'\r\n					<div class=\"separator-container\">\r\n						<div class=\"separator line-separator\"><span>\'.rex::getProperty(\'sep_char\').\'</span></div>\r\n					</div>\r\n					REX_VALUE[id=\"3\" prefix=\"<h5>\" suffix=\"</h5>\"]\';\r\n				}\r\n\r\n			echo \'\r\n            </div>\r\n        </div>\r\n  